/**
 * Declarações de tipos para arquivos de mídia
 * Permite importar vídeos, imagens e outros arquivos de mídia no TypeScript
 */

declare module '*.mp4' {
  const src: string;
  export default src;
}

declare module '*.webm' {
  const src: string;
  export default src;
}

declare module '*.ogg' {
  const src: string;
  export default src;
}

declare module '*.avi' {
  const src: string;
  export default src;
}

declare module '*.mov' {
  const src: string;
  export default src;
}




